import { Component, OnInit } from '@angular/core';
import { TokenService } from 'src/app/services/token.service';
import { HostListener } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css'],
})
export class HeaderComponent implements OnInit {
  token: string | null = null;
  isScrolled: boolean = false;

  constructor(private tokenService: TokenService) {}

  @HostListener('window:scroll', ['$event.target'])
  scroll(e: any) {
    let scroll = window.scrollY;
    if (scroll > 50) {
      this.isScrolled = true;
    } else if (scroll === 0) {
      this.isScrolled = false;
    }
  }

  ngOnInit(): void {
    this.tokenService.token$.subscribe((token) => {
      this.token = token;
    });
  }

  logout() {
    // Notify TokenService about token removal

    this.tokenService.setToken(null);
  }
}
<ng-template #loginButton>
  <a
    href="http://localhost:8888/login"
    class="bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-4 rounded-full inline-block transition duration-300 ease-in-out"
  >
    Login to Spotify
  </a>
</ng-template>

<ng-template #logoutButton>
  <a href="http://localhost:4200/">
    <button
      (click)="logout()"
      [class.bg-spotify_artistcard_bg]="isScrolled"
      class="bg-green-500 hover:bg-green-900 text-white font-semibold py-2 px-4 rounded-2xl focus:outline-none focus:ring focus:ring-gray-300"
    >
      Log out
    </button>
  </a>
</ng-template>

<div
  [class.bg-spotify_artistcard_bg]="!isScrolled"
  [class.bg-green-600]="isScrolled"
  class="p-5 text-gray-200 fixed w-full scroll-smooth transition-all duration-500"
>
  <div *ngIf="!token">
    <ng-container *ngTemplateOutlet="loginButton"></ng-container>
  </div>
  <div class="flex flex-row space-x-8 justify-end items-center" *ngIf="token">
    <app-navbar />
    <ng-container *ngTemplateOutlet="logoutButton"></ng-container>
  </div>
</div>
